var prevAudio = null;
var prevId = null;

function play(e,t) {
	if (document.getElementById(e), e.paused) {
		if (prevAudio != null) {
			prevAudio.pause();
			prevAudio.currentTime = 0;
			$("#" + prevId + ">i").removeClass("fa-pause").addClass("fa-play");
		}
		prevAudio = e;
		prevId = t;
		e.volume = volumeslider.value;
		volumeslider = document.getElementById("volumeslider");
		volumeslider.addEventListener("mousemove", setvolume);
		function setvolume(){
			e.volume = volumeslider.value / 1;
		}
		e.play();
		$("#" + t + ">i").removeClass("fa-play").addClass("fa-pause");
		var a = e.duration;
		setTimeout(function() {
			$("#" + t + ">i").removeClass("fa-pause").addClass("fa-play");
			//1e3 = 1s
		}, 1e3 * a);
	} else {
		e.pause();
		e.currentTime = 0;
		$("#" + t + ">i").removeClass("fa-pause").addClass("fa-play");
	}
}